/**
 * The Component Object
 */
function Component(){
    this.name = "";
    this.type = "";
    this.description = "";
    this.created = "";
    this.inputCallback = "";
    this.processCallback = "";
    this.outputCallback = "";
    this.config = "";
};

Component.prototype.populate = function(obj){
    this.name = (obj.name) ? obj.name : "";
    this.type = (obj.type) ? obj.type : "";
    this.description = (obj.description) ? obj.description : "";
    this.created = (obj.created) ? obj.created : "";
    this.inputCallback = (obj.inputCallback) ? obj.inputCallback : "";
    this.processCallback = (obj.processCallback) ? obj.processCallback : "";
    this.outputCallback = (obj.outputCallback) ? obj.outputCallback : "";
    this.config = (obj.config) ? obj.config : "";
    if(obj._id)
        this._id = obj._id;
};

module.exports = Component;